mutation customerUpdate($input: CustomerInput!) {
  customerUpdate(input: $input) {
    userErrors {
      field
      message
    }
    customer {
      id
      email
      phone
      firstName
      lastName
      displayName
      createdAt
      updatedAt
      state
      note
      tags
      taxExempt
      verifiedEmail
      locale
    }
  }
}
