mutation productCreate($product: ProductCreateInput!) {
  productCreate(product: $product) {
    userErrors {
      field
      message
    }
    product {
      id
      title
      handle
      descriptionHtml
      productType
      vendor
      status
      createdAt
      updatedAt
      tags
      options {
        id
        name
        position
        values
      }
      variants(first: 10) {
        nodes {
          id
          title
          price
          sku
        }
      }
    }
  }
}
